INSERT INTO `mg_page` (`id`, `parent_url`, `parent`, `title`, `url`, `html_content`, `meta_title`, `meta_keywords`, `meta_desc`, `sort`, `print_in_menu`, `invisible`, `without_style`) VALUES
(1, '', 0, 'Главная', 'index', '<h3 class=\"c-title\" style=\"text-align: center;\">\n	<span style=\"font-size:20px;\">О магазине</span>\n</h3>\n\n<div>\n	<p style=\"text-align: justify;\">\n		<span style=\"font-size:16px;\">Мы стабильная и надежная компания, с каждым днем наращиваем свой потенциал. Имеем огромный опыт в сфере корпоративных продаж, наши менеджеры готовы предложить Вам высокий уровень сервиса, грамотную консультацию, выгодные условия работы и широкий спектр цветовых решений. В число наших постоянных клиентов входят крупные компании.</span>\n	</p>\n\n	<p style=\"text-align: justify;\">\n		<span style=\"font-size:16px;\">Наши товары производятся только из самых качественных материалов!</span>\n	</p>\n\n	<p style=\"text-align: justify;\">\n		<span style=\"font-size:16px;\">Отдел корпоративных продаж готов предложить Вам персонального менеджера, грамотную консультацию, доставку на следующий день после оплаты, сертификаты на всю продукцию, индивидуальный метод работы.</span>\n	</p>\n\n	<p style=\"text-align: justify;\">\n		<span style=\"font-size:16px;\">Отдельным направлением является работа с частными лицами с оперативной доставкой, низкими ценами и высоким качеством обслуживания.</span>\n	</p>\n\n	<p style=\"text-align: justify;\">\n		<span style=\"font-size:16px;\">Главное для нас — своевременно удовлетворять потребности наших клиентов всеми силами и доступными нам средствами. Работая с нами, Вы гарантированно приобретаете только оригинальный товар подлинного качества.</span>\n	</p>\n\n	<p style=\"text-align: justify;\">\n		<span style=\"font-size:16px;\">Мы работаем по всем видам оплат. Только приобретая товар у официального дилера, Вы застрахованы от подделок. Будем рады нашему долгосрочному сотрудничеству.</span>\n	</p>\n\n	<p style=\"text-align: justify;\">\n		<span style=\"font-size:16px;\">** Информация представленная на сайте является демонстрационной для ознакомления с Moguta.CMS. <a href=\"https://moguta.ru/\">Moguta.CMS - простая cms для интернет-магазина.</a></span>\n	</p>\n</div>', 'Главная', 'Главная', '', 0, 0, 1, 0),
(2, '', 0, 'Доставка и оплата', 'dostavka', '<div>\n	<h1>\n		Доставка и оплата\n	</h1>\n\n	<p>\n		Курьером по Москве\n	</p>\n\n	<p>\n		Доставка осуществляется по Москве бесплатно, если сумма заказа составляет свыше 3000 руб. Стоимость доставки меньше чем на сумму 3000 руб. Составляет 700 руб. Данный способ доставки дает вам возможность получить товар прямо в руки, курьером по Москве. Срок доставки до 24 часов с момента заказа товара в интернет - магазине.\n	</p>\n\n	<p>\n		Доставка по России\n	</p>\n\n	<p>\n		Доставка по России осуществляется с помощью почтово – курьерских служб во все регионы России. Стоимость доставки зависит от региона и параметров товара. Рассчитать стоимость доставки Вы сможете на официальном сайте почтово – курьерской службы Почта-России и т.д. Сроки доставки составляет до 3-х дней с момента заказа товара в интернет – магазине.\n	</p>\n\n	<h2>\n		Способы оплаты:\n	</h2>\n\n	<p>\n		Наличными: Оплатить заказ товара Вы сможете непосредственно курьеру в руки при получение товара.\n	</p>\n\n	<p>\n		Наложенным платежом: Оплатить заказ товара Вы сможете наложенным платежом при получение товара на складе. С данным видом оплаты Вы оплачиваете комиссию за пересылку денежных средств.\n	</p>\n\n	<p>\n		Электронными деньгами: VISA, Master Card, Yandex.Деньги, Webmoney, Qiwi и др.\n	</p>\n</div>\n\n<div>\n	&nbsp;\n</div>\n\n<div>\n	&nbsp;\n</div>\n\n<div>\n	&nbsp;\n</div>', 'Доставка', 'Доставка', 'Доставка осуществляется по Москве бесплатно, если сумма заказа составляет свыше 3000 руб.  Стоимость доставки меньше чем на сумму 3000 руб. Составляет 700 руб.', 2, 1, 0, 0),
(3, '', 0, 'Обратная связь', 'feedback', '<p>Свяжитесь с нами, посредством формы обратной связи представленной ниже. Вы можете задать любой вопрос, и после отправки сообщения наш менеджер свяжется с вами.</p>', 'Обратная связь', 'Обратная связь', 'Свяжитесь с нами, по средствам формы обратной связи представленной ниже. Вы можете задать любой вопрос, и после отправки сообщения наш менеджер свяжется с вами.', 3, 1, 0, 0),
(4, '', 0, 'Контакты', 'contacts', '<h1 class=\"new-products-title\">\n	Контакты\n</h1>\n\n<p>\n	<strong>Наш адрес </strong>г. Санкт-Петербург Невский проспект, дом 3\n</p>\n\n<p>\n	<strong>Телефон отдела продаж </strong>8 (555) 555-55-55\n</p>\n\n<p>\n	Пн-Пт 9.00 - 19.00\n</p>\n\n<p>\n	Электронный ящик: <span style=\"line-height: 1.6em;\">info@sale.ru</span>\n</p>\n[mg-yandex-map-marks] [mg-yandex-map-address-list]', 'Контакты', 'Контакты', 'Мы в социальных сетях  Мы в youtoube', 5, 1, 0, 0),
(5, '', 0, 'Каталог', 'catalog', 'В каталоге нашего магазина вы найдете не только красивые и необычные комнатные растения, но и необходимые аксессуары для ухода.', 'Каталог', 'Каталог', '', 1, 1, 0, 0),
